  roomNumber = #{roomnumber,jdbcType=VARCHAR},
  roomPeople = #{roompeople,jdbcType=VARCHAR},
  roomPhone = #{roomphone,jdbcType=VARCHAR}
where roomId = #{roomid,jdbcType=BIGINT}

update room
set roomBuilding=#{build},
roomNumber=#{name},
roomPeople=#{people},
roomPhone=#{tel}

where roomId=#{id}

mapper.select:

select roomId, roomBuilding, roomNumber, roomPeople, roomPhone
from room
where roomId = #{roomid,jdbcType=BIGINT}
